# sitecustomize.py: to be placed in lib/pythonX.Y/site-packages
# See https://docs.python.org/3/library/site.html
# add pytraj and pdb4amber in sys.path
import sys
import os
easyfile='easy-install.pth'
for path in sys.path:
    if os.path.exists(os.path.join(path,easyfile)):
        with open(os.path.join(path,easyfile)) as f:
            for line in f:
                newpath=os.path.join(path,line[:-1])
                if os.path.exists(newpath) and newpath not in sys.path:
                    sys.path.append(newpath)
